.upez-watermark {
  visibility: visible !important;
  opacity: 1 !important;
  width: 101px !important;
  height: 12px !important;
  margin: 9px 0 auto auto !important;
}
.upez-watermark img {
  border-radius: 0;
}

.upez-watermark picture,
.upez-watermark img {
  visibility: visible !important;
  height: 100% !important;
  opacity: 1 !important;
}

.with-upez-loader {
  position: relative;
  --uib-color: white;
  transition: color .3s;
}
.with-upez-loader--black {
  --uib-color: black;
}

.with-upez-loader[adding] {
  color: transparent !important;
  transition-delay: 0s;
}
[adding] .upez-loader {
  width: var(--uib-size);
}

[adding] .upez-loader--dots {
  opacity: 1;
  transform: translate(-50%, -50%);
}
[adding] .upez-loader--spinner {
  height: var(--uib-size);
  width: var(--uib-size);
}

.upez-loader--spinner {
  /* --uib-size: 40px; */
  --uib-speed: 2s;
  --uib-color: rgba(0,0,0,0.5);
  height: 0;
  width: 0;

  vertical-align: middle;
  transform-origin: center;
  animation: rotate var(--uib-speed) linear infinite;
}

.upez-loader--spinner circle {
  fill: none;
  stroke: var(--uib-color);
  stroke-dasharray: 1, 200;
  stroke-dashoffset: 0;
  stroke-linecap: round;
  animation: stretch calc(var(--uib-speed) * 0.75) ease-in-out infinite;
}

.upez-loader--dots {
  width: 30px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, 0%);
  opacity: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  aspect-ratio: 2;
  --_g: no-repeat radial-gradient(circle closest-side, var(--uib-color) 90%, #ffffff00);
  background: 
    var(--_g) 0%   50%,
    var(--_g) 50%  50%,
    var(--_g) 100% 50%;
  background-size: calc(100%/3) 50%;
  animation: l3 1s infinite linear;
  transition: opacity .3s cubic-bezier(0.22, 1, 0.36, 1), transform .3s cubic-bezier(0.22, 1, 0.36, 1);
}

@keyframes l3 {
  20%{background-position:0%   0%, 50%  50%,100%  50%}
  40%{background-position:0% 100%, 50%   0%,100%  50%}
  60%{background-position:0%  50%, 50% 100%,100%   0%}
  80%{background-position:0%  50%, 50%  50%,100% 100%}
}

.upez-loader {
  width: 0;
  max-width: 60px;
  transition: width .3s;
  --uib-size: 80%;
  --uib-speed: 1.4s;
  --uib-stroke: 5px;
  --uib-bg-opacity: .1;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  align-items: center;
  justify-content: center;
  height: var(--uib-stroke);

  border-radius: calc(var(--uib-stroke) / 2);
  overflow: hidden;
}

.upez-loader::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  background-color: var(--uib-color);
  opacity: var(--uib-bg-opacity);
  transition: background-color 0.3s ease;
}

.upez-loader::after {
  content: '';
  height: 100%;
  width: 100%;
  border-radius: calc(var(--uib-stroke) / 2);
  animation: zoom var(--uib-speed) ease-in-out infinite;
  transform: translateX(-100%);
  background-color: var(--uib-color);
  transition: background-color 0.3s ease;
}

@keyframes zoom {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

