}

.upez__marquee {
  position: relative;
  overflow: hidden;
  --offset: 20vw;
  --move-initial: calc(-25% + var(--offset));
  --move-final: calc(-75% + var(--offset));
}

.upez__marquee__inner {
  width: max-content;
  display: flex;
  position: relative;
  transform: translate3d(var(--move-initial), 0, 0);
  animation: upez__marquee 25s linear infinite;
  animation-play-state: paused;
}

.upez__marquee .upez__marquee__inner > div {
  padding: 0 2vw;
}

[cart-open="true"] .upez__marquee .upez__marquee__inner {
  animation-play-state: running;
}

@keyframes upez__marquee {
  0% {
    transform: translate3d(var(--move-initial), 0, 0);
  }

  100% {
    transform: translate3d(var(--move-final), 0, 0);
  }
}

@keyframes grid-slide-down {
	0% {
		grid-template-rows: 0fr;
		opacity: 0;
	}
    50% {
        opacity: 0;
        grid-template-rows: 1fr;
    }
	100% {
		grid-template-rows: 1fr;
		opacity: 1;	
	}
}

.animate--grid-slide-down {
    animation: grid-slide-down .7s cubic-bezier(0.22, 1, 0.36, 1);
}

.upez--hide-when-empty:empty {
  display: none !important;
}

#gorgias-chat-container {
  z-index: 0;
  position: relative;
}

.upez--checkbox-container {
  position: relative;
}
.upez--checkbox-container > input[type="checkbox"] {
  position: absolute;
  opacity: 0;
}
.upez--checkbox-container .upez--checkmark:after {
  left: 35%;
  top: 20%;
  width: 25%;
  height: 50%;
  border: solid white;
  border-width: 0 2px 2px 0;
  -webkit-transform: rotate(45deg);
  -ms-transform: rotate(45deg);
  transform: rotate(45deg);
}
.upez--checkmark:after {
  content: "";
  position: absolute;
  display: none;
}
.upez--checkbox-container input[type="checkbox"]:checked ~ .upez--checkmark:after {
  display: block;
}
.upez--checkbox-container input[type="checkbox"]:checked ~ .upez--checkmark {
  background-color: currentColor;
}
.upez--checkmark {
  position: absolute;
  top: 0;
  left: 0;
  height: var(--upez-checkmark-size);
  width: var(--upez-checkmark-size);
  border: 1px solid currentColor;
  border-radius: var(--upez-corner-base);
}

#upezCart svg {
  fill: none;
  stroke: inherit;
}

/* Only show 3 quick payment options */
.shopify-cleanslate ul > li:not(li:first-of-type):not(li:nth-of-type(2)):not(li:nth-of-type(3)) {
  display: none;
}

#smile-ui-lite-container {
